INSERT INTO question_bank (id, subject, grade_level, difficulty, category, question_text, options_json, correct_index, explanation) VALUES (570, 'ANGLAIS', 'CM2'::grade_level_type, 'MEDIUM'::difficulty_type, 'VERBES', 'Que signifie la phrase : ''I am reading a book'' ?', '["Je lis un livre (souvent)", "Je vais lire un livre", "Je suis en train de lire un livre", "J''ai lu un livre"]'::jsonb, 2, 'La structure ''be + verbe-ing'' exprime une action qui se passe en ce moment.');
INSERT INTO question_bank (id, subject, grade_level, difficulty, category, question_text, options_json, correct_index, explanation) VALUES (571, 'ANGLAIS', 'CM2'::grade_level_type, 'HARD'::difficulty_type, 'COMPARAISON', 'Comment dit-on ''Un éléphant est plus grand qu''une souris'' ?', '["An elephant is more big than a mouse", "An elephant is bigger than a mouse", "An elephant is big than a mouse", "An elephant is biger then a mouse"]'::jsonb, 1, 'Pour les adjectifs courts, on ajoute ''-er'' à la fin pour comparer deux choses.');
